import React, { useEffect, useState, useRef } from "react";
import { Api } from "./api";
import ViewUser from "./assets/view.png";
import AddUser from "./assets/adduser.png";
import UpdateUser from "./assets/update.png";
import CreateUser from "./create-user";

function App() {
  const [user, setUser] = useState([]);
  const [create, setCreate] = useState(false);
  const buttonRef = useRef(null)
  const inputRef = useRef(null)
  const FetchUserData = async () => {
    const data = await Api('https://api.escuelajs.co/api/v1/users')
    if(data){
      console.log({data});
      setUser(data)
    }
  }

  const addUser = () => {
   setCreate(true);
   debugger;
   if(buttonRef?.current){
    buttonRef.current.style.color="red";
   }
   if(inputRef?.current){

    inputRef.current.focus();
   }
  }
  const closePop = () => {
    setCreate(false)
  }
  useEffect(() => {
    FetchUserData();
  }, [])
  return (
    <div className="App">
        <div className="table">
        <div className="adduser">
          <span onClick={addUser}>Add User <i><img src={AddUser} alt="" /></i></span>
        </div>
          <table>
            <thead>
              <tr>
                <td ref={buttonRef}>Id</td>
                <td>Name</td>
                <td>Email</td>
                <td>Role</td>
                <td>Avatar</td>
                <td>Actions</td>
              </tr>
            </thead>
            <tbody>
            {user.map(item => (
              <tr key={item.id}Â >
                <td>{item.id}</td>
                <td>{item.name}</td>
                <td>{item.email}</td>
                <td>{item.role}</td>
                <td><img src={item.avatar} alt="" /></td>
                <td>
                  <i><img src={ViewUser} alt="View User" /></i>
                  <i><img src={UpdateUser} alt="View User" /></i>
                </td>
              </tr>
            ))}
            </tbody>
          </table>
        </div> 
        {create && <CreateUser closePop={closePop} FetchUserData={FetchUserData} inputRef={inputRef} />}

    </div>
  );
}

export default App;
