import React, { useEffect, useState } from 'react'
import { Api } from './api';
import viewIcon from './assets/view.png'
import EditIcon from './assets/edit.png'
import AddIcon from './assets/adduser.png'

const Home = () => {
    document.title = "React Project 3";
    const [users, setUsers] = useState([]);
    const [createUser, setCreateUser] = useState(false)


    const fetchUsersData = async () => {
        const data = await Api('https://api.escuelajs.co/api/v1/users')
        if (data) {
            setUsers(data);
        }
    }

    const createUserOnclick = () => {
        setCreateUser()
    }

    useEffect(() => {
        fetchUsersData();
    }, [])
  return (
    <>
        <div className="homePage">
        {createUser && (
            <div className="createUserPop">
            <form action="">
                <fieldset>
                    <legend>Create User</legend>
                    <input type="text" name='' value='' onChange='' placeholder='Enter name' />
                    <input type="text" name='' value='' onChange='' placeholder='Enter email' />
                    <button type="submit">Create User</button>
                </fieldset>
            </form>
        </div>
        )}
        <div className="adduser">
          <span onClick={createUserOnclick}>Create User <i><img src={AddIcon} alt="" /></i></span>
        </div>
            <table border='1px solid #000'>
                <thead>
                    <tr>
                        <td>Id</td>
                        <td>Name</td>
                        <td>Email</td>
                        <td>Role</td>
                        <td>Avatar</td>
                        <td>Actions</td>
                    </tr>
                </thead>
                <tbody>
                {users.map((item) => (
                    <tr id={item.id}>
                        <td>{item.id}</td>
                        <td>{item.name}</td>
                        <td>{item.email}</td>
                        <td>{item.role}</td>
                        <td><img src={item.avatar} alt="" /></td>
                        <td>
                            <i><img src={viewIcon} alt="" /></i>
                            <i><img src={EditIcon} alt="" /></i>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    </>
  )
}

export default Home
